var fecha = new Date();

msg.fecha = fecha.toLocaleDateString("es-ES", { weekday: "long", year: "numeric", month: "long", day: "numeric" });


return msg;

// Obtener o inicializar el estado global
const alerts = global.get('alerts') || {};
const cylinder = msg.nombre;
const pressure = msg.payload;

// Definir rangos
const ranges = {
    normal: { min: 100, max: 200 },
    warning: { min: 60, max: 90 },
    alarm: { min: 0, max: 59 }
};

// Determinar estado
let status, message, color;
if (pressure >= ranges.normal.min && pressure <= ranges.normal.max) {
    status = 'normal';
} else if (pressure >= ranges.warning.min && pressure <= ranges.warning.max) {
    status = 'warning';
    message = `Advertencia: Presión baja ${pressure} PSI`;
    color = '#59544c';
} else if (pressure >= ranges.alarm.min && pressure <= ranges.alarm.max) {
    status = 'alarm';
    message = `¡Alarma! Presión crítica ${pressure} PSI`;
    color = '#a32929';
}

// Validar y actualizar estado de alertas
if (status === 'normal') {
    delete alerts[cylinder]; // Eliminar alerta si está en estado normal
} else {
    // Asegurarse de que el cilindro y los valores sean válidos
    if (cylinder && cylinder !== 'undefined' && message && color) {
        alerts[cylinder] = { message, color }; // Agregar o actualizar alerta
    }
}

// Guardar en el contexto global
global.set('alerts', alerts);

// Generar arreglo limpio de alertas
msg.alerts = Object.entries(alerts)
    .filter(([key, value]) => key && key !== 'undefined' && value.message && value.color) // Filtrar alertas válidas
    .map(([key, value]) => ({ cylinder: key, ...value })); // Formatear las alertas

return msg;

