    status = 'normal';
} else if (pressure >= ranges.warning.min && pressure <= ranges.warning.max) {
    status = 'warning';
    message = `Advertencia: Presi칩n baja ${pressure} PSI`;
    color = '#59544c';
} else if (pressure >= ranges.alarm.min && pressure <= ranges.alarm.max) {
    status = 'alarm';
    message = `춰Alarma! Presi칩n cr칤tica ${pressure} PSI`;
    color = '#a32929';
}

